CREATE TABLE `book_review` (
  `sid` int(11) NOT NULL,
  `account` varchar(255) NOT NULL,
  `category` varchar(255) NOT NULL,
  `booktitle` varchar(15) NOT NULL,
  `ranking` int(11) NOT NULL,
  `review` mediumtext NOT NULL,
  `creatdate` datetime NOT NULL
